pragma solidity ^0.4.18;

import './Relay.sol'; // implicitly imports PublicProxy, TrustedProxy, and Token also
import './Logger.sol';
import './SafeMath.sol';
import './Ownable.sol';

/* A token-agnostic implementation of a contract to manage token-based memberships */
contract MembershipController is Ownable {
    using SafeMath for uint;
    using Relay for Relay.Data;
    Relay.Data relayLib;

    /* The worker of the contract. */
    address public worker;

    /* The address of the ERC20 contract whose tokens will be locked by this manager. */
    Token public token;

    /* The address for the public relay to call the activate function. */
    address public activateRelay;

    /* The address for the public relay to call the retrieve function. */
    address public retrieveRelay;

    /* The address for the public relay to call the override function. */
    address public overrideRelay;

    /* The address for the central logger contract. */
    Logger public logger;

    /* The address that is allowed to deprecate this contract. */
    address public deprecator;

    /* Tier level requirements determined by the owner during creation. */
    uint[] public tiers;

    /* The membership period duration in seconds. */
    uint public membershipPeriodDuration;

    /* Total amount locked in active memberships through this contract. */
    uint public totalLocked;

    /* The boolean that signifies a deprecated MembershipController contract. */
    bool public deprecated;

    /* Mutex */
    bool private locked;

    /* Stores all relevant information for a locked position.
     *
     * tier: stores the tier level the user qualifies for upon locking.
     *
     * timeStarted: records time user activated their membership
     *
     * timeCancelled: records time user requested auto-renew cancellation
     *
     * timeUntilExpiration: stores the duration of time from timeCancelled until the current period expires
     *
     * cancelled: records whether a cancellation has been requested
     *
     * overrideApproved: whether the worker has approved
     */
    struct Membership {
        uint tier;
        uint timeStarted;
        uint timeCancelled;
        uint timeUntilExpiration;
        bool cancelled;
        bool overrideApproved;
    }

    /**
     *  Maps relay addresses -> Memberships
     */
    mapping (address => Membership) public memberships;

    /**
     *  Constructor function assigns owner, worker, token, logger, deprecator, tiers, and duration of membership period (in seconds).
     *      - Tiers should be passed in [lowest, ..., highest]
     *      - membershipPeriodDuration is the duration of each membership period, and is assumed to be a number of seconds
     *          - 2592000 seconds = 30 days
     *
     *  Also initializes the public activate, retrieve, and override relays
     */
    function MembershipController(address _worker, address _token, address _logger, address _deprecator, uint[] _tiers, uint _membershipPeriodDuration) public {
        // enforce invariant that tiers are entered in ascending order
        for (uint i; i < _tiers.length - 1; i++) {
            for (uint j = i + 1; j < _tiers.length; j++) {
                require(_tiers[i] < _tiers[j]);
            }
        }

        worker = _worker;
        token = Token(_token);
        logger = Logger(_logger);
        deprecator = _deprecator;
        tiers = _tiers;
        membershipPeriodDuration = _membershipPeriodDuration;
        initializeRelays();
    }

    /***************************************
     *********** Private helpers ************
     ***************************************/

    function initializeRelays() private {
        relayLib.addPublicRelay(this.activate.selector);
        relayLib.addPublicRelay(this.retrieve.selector);
        relayLib.addPublicRelay(this.override.selector);
        activateRelay = relayLib.getRelay(this.activate.selector, this);
        retrieveRelay = relayLib.getRelay(this.retrieve.selector, this);
        overrideRelay = relayLib.getRelay(this.override.selector, this);
    }

    function getRelay(address _user) private view returns (address) {
        // validate
        address relay = relayLib.getRelay(this.cancelAndRetrieve.selector, _user);
        // needs to initialize their relay
        if (relay == 0x0) revert();

        return relay;
    }

    function activateMembership(Membership storage membership, uint tier) private {
        // allow reactivating/upgrading cancelled membership for user
        if (membership.cancelled) {

            // if upgrading, add newly locked tokens to totalLocked
            if (tier > membership.tier) {
                totalLocked = totalLocked.add(tiers[tier].sub(tiers[membership.tier]));
            }

            // reset cancellation fields
            membership.cancelled = false;
            membership.timeCancelled = 0;
            membership.timeUntilExpiration = 0;
        }
        else if (membership.timeStarted != 0) {
            // revert if already locked at this tier
            if (tier == membership.tier) revert();
            // only add upgraded difference
            else {
                totalLocked = totalLocked.add(tiers[tier].sub(tiers[membership.tier]));
            }
        }
        else {
            totalLocked = totalLocked.add(tiers[tier]);
        }

        membership.tier = tier;
        membership.timeStarted = now;
    }

    /***************************************
     *********** Query helpers *************
     ***************************************/

    function getUserRelay(address _user) public view returns (address) {
        return relayLib.getRelay(this.cancelAndRetrieve.selector, _user);
    }

    /***************************************
     ******** Deprecation methods **********
     ***************************************/

    modifier onlyDeprecator {
        if (msg.sender != deprecator) revert();
        _;
    }

    modifier ifNotDeprecated {
        if (deprecated) revert();
        _;
    }

    function deprecate() public onlyDeprecator ifNotDeprecated {
        deprecated = true;
    }

    /***************************************
     ********* New User methods ************
     ***************************************/

    modifier onlyNewUser {
        if (relayLib.getRelay(this.cancelAndRetrieve.selector, msg.sender) != 0x0) revert();
        _;
    }

    /**
     *  Allow users to initialize their personal relay by sending 0 ETH to this contract
     *
     *  Relays will be reused for each address; relays are reset upon retrieval (post-cancellation or through override)
     *  Resetting only sets it to a default state; original owner will always be the owner of any given relay.
     */
    function () public onlyNewUser ifNotDeprecated {
        relayLib.addRelay(this.cancelAndRetrieve.selector, msg.sender, msg.sender, address(token));
        logger.logUserRelayCreated(relayLib.getRelay(this.cancelAndRetrieve.selector, msg.sender), msg.sender, msg.sender);
    }

    /***************************************
     ********** Worker methods **************
     ***************************************/

    modifier onlyWorker {
        if (msg.sender != worker) revert();
        _;
    }

    /**
     *  Allows worker to create a relay on behalf of a user
     */
    function createRelayForUser(address _user, address _recipient) public onlyWorker ifNotDeprecated {
        // make sure user doesn't already have a relay
        if (relayLib.getRelay(this.cancelAndRetrieve.selector, _user) != 0x0) revert();
        if (_recipient == 0x0) {
            _recipient = _user;
        }

        relayLib.addRelay(this.cancelAndRetrieve.selector, _user, _recipient, address(token));
        logger.logUserRelayCreated(relayLib.getRelay(this.cancelAndRetrieve.selector, _user), _user, _recipient);
    }

    /**
     *  Allows activating at a specific tier
     */
    function activateAtTier(address _user, uint _tier) public onlyWorker ifNotDeprecated {
        // revert if tier doesn't exist
        require(_tier < tiers.length);

        address relay = getRelay(_user);

        // retrieve stored membership info
        Membership storage membership = memberships[relay];

        // throw if attempting to downgrade
        require(_tier >= membership.tier);

        // retrieve relay's token balance and required amount of tokens for requested tier
        uint balance = token.balanceOf(relay);
        uint requiredAmount = tiers[_tier];

        // revert if relay has fewer tokens than required amount for tier
        require(balance >= requiredAmount);

        activateMembership(membership, _tier);

        logger.logMembershipActivated(relay, membership.tier, membership.timeStarted);
    }

    /***************************************
     ******** Public Relay methods *********
     ***************************************/

    modifier onlyActivateRelay {
        if (msg.sender != activateRelay) revert();
        _;
    }

    modifier onlyRetrieveRelayOrWorker {
        if (msg.sender != retrieveRelay && msg.sender != worker) revert();
        _;
    }

    modifier onlyOverrideRelayOrWorker {
        if (msg.sender != overrideRelay && msg.sender != worker) revert();
        _;
    }

    /**
     *  Allows user to activate membership via a public relay
     */
    function activate(address _user) public onlyActivateRelay ifNotDeprecated {
        address relay = getRelay(_user);

        Membership storage membership = memberships[relay];

        // determine tier based on current tier limits
        // check if they qualify for highest tier first
        uint balance = token.balanceOf(relay);
        bool activated;
        for (int i = int(tiers.length - 1); i >= 0; i--) {
            if (balance >= tiers[uint(i)]) {

                activateMembership(membership, uint(i));
                activated = true;

                break;
            }
        }

        // if balance < minimum tier requirement
        if (!activated) revert();
        // else..
        logger.logMembershipActivated(relay, membership.tier, membership.timeStarted);
    }

    /**
     *  Allows user to retrieve any unlocked tokens from their relay
     */
    function retrieve(address _user) public onlyRetrieveRelayOrWorker {
        address relay = getRelay(_user);
        Membership storage membership = memberships[relay];

        uint balance = token.balanceOf(relay);
        // if relay has no tokens, throw
        if (balance == 0) revert();

        // if locked, only retrieve not locked
        if (membership.timeStarted != 0) {
            // calculate amount to transfer
            uint lockedAmount = tiers[membership.tier];
            uint transferAmount = balance - lockedAmount;
            // all available tokens are locked so revert
            if (transferAmount == 0) revert();
            TrustedProxy(relay).retrieveTokens(transferAmount);
            logger.logExcessTokensRetrieved(relay, transferAmount);
        }
        // if not locked, retrieve all
        else {
            TrustedProxy(relay).retrieveTokens();
            logger.logExcessTokensRetrieved(relay, balance);
        }
    }

    /**
     *  Multisig function to allow token retrieval bypassing timeout
     *
     *  Only to be used by support.
     */
    function override(address _user) public onlyOverrideRelayOrWorker {
        address relay = getRelay(_user);
        Membership storage membership = memberships[relay];
        // not locked, so no need for this function; revert
        if (membership.timeStarted == 0) revert();

        if (msg.sender == worker) {
            membership.overrideApproved = true;
        }
        else if (membership.overrideApproved) {
            // adjust global totals
            totalLocked = totalLocked.sub(tiers[membership.tier]);

            // reset this relay's membership
            delete memberships[relay];

            // transfer all tokens to recipient
            TrustedProxy(relay).retrieveTokens();

            // log event
            logger.logMembershipTokensRetrieved(relay, now);
        }
        else revert();
    }

    /***************************************
     ********** Mutex methods **************
     ***************************************/

    modifier lock {
        if (locked) revert();
        locked = true;
        _;
        locked = false;
    }

    /**
     *  Allows user's relay to cancel their membership and retrieve tokens after their current membership period has expired
     *
     *  This can be public because only relays will be included in the memberships mapping, so there's no risk.
     *
     *  Lock behind a mutex to prevent re-entry just to be safe, though even in the worst case, a malicious user could
     *  never retrieve more tokens than their relay contains.
     */
    function cancelAndRetrieve() public lock {
        // first call to this function should cancel a membership
        // second call should retrieve tokens assuming appropriate time has expired

        Membership storage membership = memberships[msg.sender];
        // not locked so revert
        if (membership.timeStarted == 0) revert();
        // 1st call: needs to be cancelled
        if (!membership.cancelled) {
            membership.timeCancelled = now;
            membership.timeUntilExpiration = membershipPeriodDuration - ((membership.timeCancelled - membership.timeStarted) % membershipPeriodDuration);
            membership.cancelled = true;

            // log event
            logger.logMembershipCancelled(msg.sender, (membership.timeCancelled + membership.timeUntilExpiration));
        }
        // 2nd call: retrieves tokens if membership has expired
        else if (now >= membership.timeCancelled + membership.timeUntilExpiration) {
            // adjust global totals
            totalLocked = totalLocked.sub(tiers[membership.tier]);

            // reset this relay's membership
            delete memberships[msg.sender];

            // transfer all tokens to recipient
            TrustedProxy(msg.sender).retrieveTokens();

            // log event
            logger.logMembershipTokensRetrieved(msg.sender, now);
        }
        // throw if the membership has not expired yet
        else revert();
    }

    /***************************************
     ********** Owner methods **************
     ***************************************/

    function transferWorker(address _worker) public onlyOwner {
        if (_worker == 0x0) revert();
        worker = _worker;
    }
}
