pragma solidity ^0.4.18;

import './Token.sol';
import './MembershipController.sol';

/**
 *  The TrustedProxy contract represents a single method on a host contract. This one is specifically designed for use with the MembershipController.
 *
 *  It stores the address of the host contract and the method id of the method so that it can invoke the method when a user sends funds to this contract's address.
 *
 *  It stores a recipient address that will receive any retrieved tokens.
 *
 *  Note: This version is a Trusted variant of the standard Proxy contract that allows an authorized address to call functions in addition to the owner.
 */
contract TrustedProxy {

    /* The address of the relay owner (who has permission to trigger it). */
    address public owner;

    /* The address that retrieved tokens will be transferred to. */
    address public recipient;

    /* The address of the host contract. */
    address public host;

    /* The address of the token contract. */
    Token token;

    /**
     *  The methodId of the host contract method.
     *
     *  This is equivalent to bytes4(keccak256(methodName)),
     *  where the method name includes the parentheses as if you were calling the function,
     *  and the parameter types, in order, separated by commas with no whitespace included.
     *      Example: methodId = bytes4(keccak256("transfer(uint256,address)"))
     */
    bytes4 methodId;

    /* Modifiers */
    modifier onlyHost { if (msg.sender != host) revert(); _; }
    modifier onlyOwner { if (msg.sender != owner) revert(); _; }
    modifier onlyAuthorizedAddress { if (msg.sender != owner && msg.sender != MembershipController(host).worker()) revert(); _; }
    modifier validRecipient(address _recipient) { if (_recipient == 0x0 || _recipient == address(this)) revert(); _; }

    function TrustedProxy(bytes4 _methodId, address _host, address _owner, address _recipient, address _token) public validRecipient(_recipient) {
        host = _host;
        owner = _owner;
        recipient = _recipient;
        methodId = _methodId;
        token = Token(_token);
    }

    /**
     *  Called by MembershipController when retrieving all tokens post-cancellation and post-expiration and when overriding an active membership.
     */
    function retrieveTokens() public onlyHost {
        uint balance = token.balanceOf(this);
        if (!token.transfer(recipient, balance)) revert();
    }

    /**
     *  Called by MembershipController when retrieving a specific amount of tokens.
     */
    function retrieveTokens(uint _amount) public onlyHost {
        if (!token.transfer(recipient, _amount)) revert();
    }

    /**
     *  Allows an authorized address to retrieve non-membership tokens mistakenly sent to this contract.
     */
    function retrieveOtherToken(address _token) public onlyAuthorizedAddress {
        // do not allow retrieval of membership tokens through this function
        if (_token == address(token)) revert();

        uint balance = Token(_token).balanceOf(this);
        if (!Token(_token).transfer(recipient, balance)) revert();
    }

    /**
     *  Allows the owner to change the address that will receive any retrieved tokens.
     */
    function changeRecipient(address _recipient) public onlyOwner validRecipient(_recipient) {
        recipient = _recipient;
    }

    function() public onlyAuthorizedAddress {
        // if host call throws, throw this transaction as well
        if(!host.call(methodId)) revert();
    }
}
